userDefinedLinkHintCss:
div > .vimiumHintMarker {
  padding: 4px 6px;
  background-color: #282828;
  border: 0;
  border-radius: 6px;
  box-shadow: none;
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 14px;
  font-family: "Berkeley Mono", monospace;
  font-weight: bold;
  text-shadow: none;
  color: #d5c4a1;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #fabd2f;
}

ul {
  list-style: none;
  display: none;
  margin: 0;
  padding: 0;
}

#vomnibar {
  display: block;
  position: fixed;
  width: calc(100% - 20px); /* adjusted to keep border radius and box-shadow visible*/
  top: 8px;
  left: 8px;
  font-family: "Berkeley Mono", monospace;

  background: #282828;
  text-align: left;
  border-radius: 6px;
  box-shadow: none;
  border: 3px solid #98971a;
  /* one less than hint markers and the help dialog (see ../content_scripts/vimium.css). */
  z-index: 2139999999;
}

#vomnibar input {
  font-family: "Berkeley Mono", monospace;
  font-size: 20px;
  height: 34px;
  margin-bottom: 0;
  padding: 6px;
  background-color: #3c3836;
  color: #ebdbb2;
  border-radius: 3px;
  border: 0px solid #98971a;
  box-shadow: none;
  width: 100%;
  outline: none;
  box-sizing: border-box;
}

#vomnibar-search-area {
  display: block;
  padding: 10px;
  border-radius: 4px 4px 0 0;
  border-bottom: none;
}

#vomnibar ul {
  border-radius: 0 0 4px 4px;
}

#vomnibar li {
  font-family: "Berkeley Mono", monospace;
  border-bottom: none;
  line-height: 1.1em;
  padding: 7px 10px;
  font-size: 16px;
  color: #ff0000;
  position: relative;
  display: list-item;
  margin: auto;
}

#vomnibar li:last-of-type {
  border-bottom: none;
}

#vomnibar li .top-half, #vomnibar li .bottom-half {
  display: block;
  overflow: hidden;
}

#vomnibar li .bottom-half {
  font-family: "Berkeley Mono", monospace;
  font-size: 15px;
  margin-top: 3px;
  padding: 2px 0;
}

#vomnibar li .icon {
  padding: 0 13px 0 6px;
  vertical-align: bottom;
}

#vomnibar li .source {
  font-weight: bold;
  color: #ebdbb2;
  margin-right: 4px;
}
#vomnibar li .relevancy {
  position: absolute;
  right: 0;
  top: 0;
  padding: 5px;
  color: #ff0000;
  font-family: "Berkeley Mono", monospace;
  width: 100px;
  overflow: hidden;
}

#vomnibar li .url {
  white-space: nowrap;
  color: #a89984;
}

#vomnibar li .match {
  font-family: "Berkeley Mono", monospace;
  font-weight: bold;
  color: #fabd2f;
}

#vomnibar li em, #vomnibar li .title {
  color: #ebdbb2;
  margin-left: 4px;
}
#vomnibar li em {
  font-family: "Berkeley Mono", monospace;
  font-style: italic;
}
#vomnibar li em .match, #vomnibar li .title .match {
  font-family: "Berkeley Mono", monospace;
  font-weight: bold;
  color: #fabd2f;
}

#vomnibar li.selected {
  background-color: #3c3836;
  font-weight: bold;
}

#vomnibar input::selection {
  background-color: #3c3836;
}

.no-insert-text {
  visibility: hidden;
}
